import numpy as np

class GenScene(Scene):
    def construct(self):
        size = 10
        grid = np.zeros((size, size), dtype=int)
        glider = [(0,1),(1,2),(2,0),(2,1),(2,2)]
        for r,c in glider:
            grid[r+2][c+2] = 1
        def draw_grid(g):
            cells = VGroup()
            for r in range(size):
                for c in range(size):
                    sq = Square(side_length=0.5, stroke_width=0.5, color=GRAY)
                    sq.move_to(RIGHT*(c*0.5 - 2.25) + UP*(r*0.5 - 2.25))
                    if g[r][c]:
                        sq.set_fill(GREEN, opacity=0.8)
                    cells.add(sq)
            return cells
        def step(g):
            new = np.zeros_like(g)
            for r in range(size):
                for c in range(size):
                    n = sum(g[(r+dr)%size][(c+dc)%size] for dr in [-1,0,1] for dc in [-1,0,1] if not (dr==0 and dc==0))
                    if g[r][c] and n in [2,3]: new[r][c] = 1
                    elif not g[r][c] and n == 3: new[r][c] = 1
            return new
        cells = draw_grid(grid)
        self.play(Create(cells), run_time=1)
        for _ in range(8):
            grid = step(grid)
            new_cells = draw_grid(grid)
            self.play(Transform(cells, new_cells), run_time=0.4)
        self.wait()

