from manim_physics import *

class GenScene(Scene):
    def construct(self):
        lens_style = {"fill_opacity": 0.5, "color": YELLOW}
        convex_lens = Lens(-5, 1, **lens_style).shift(LEFT)
        concave_lens = Lens(5, 1, **lens_style).shift(RIGHT)
        rays = [
            Ray(LEFT * 5 + UP * i, RIGHT, 8, [convex_lens, concave_lens], color=PURPLE)
            for i in np.linspace(-2, 2, 10)
        ]

        self.play(Create(convex_lens), Create(concave_lens))
        for ray in rays:
            self.play(Create(ray))

