class GenScene(Scene):
    def construct(self):
        title = Text('Derivative Rules', font_size=48, color=YELLOW).to_edge(UP)
        rules = VGroup(
            MathTex('\\text{Power: } \\frac{d}{dx}x^n = nx^{n-1}', font_size=40),
            MathTex('\\text{Product: } (fg)\' = f\'g + fg\'', font_size=40),
            MathTex('\\text{Chain: } \\frac{d}{dx}f(g(x)) = f\'(g(x)) \\cdot g\'(x)', font_size=40),
        ).arrange(DOWN, buff=0.8)
        self.play(Write(title))
        for rule in rules:
            self.play(Write(rule))
        self.wait()

