class GenScene(Scene):
    def construct(self):
        nodes = {}
        positions = {1: [0, 2.5, 0], 2: [-2, 1, 0], 3: [2, 1, 0], 4: [-3, -0.5, 0], 5: [-1, -0.5, 0], 6: [1, -0.5, 0], 7: [3, -0.5, 0]}
        values = {1: '8', 2: '4', 3: '12', 4: '2', 5: '6', 6: '10', 7: '14'}
        node_group = VGroup()
        edge_group = VGroup()
        for key, pos in positions.items():
            circle = Circle(radius=0.4, color=BLUE, fill_opacity=0.3).move_to(pos)
            label = Text(values[key], font_size=28).move_to(pos)
            nodes[key] = VGroup(circle, label)
            node_group.add(nodes[key])
        for parent, child in [(1,2),(1,3),(2,4),(2,5),(3,6),(3,7)]:
            edge = Line(positions[parent], positions[child], color=GRAY, buff=0.4)
            edge_group.add(edge)
        self.play(Create(edge_group), run_time=1)
        self.play(LaggedStart(*[Create(n) for n in node_group], lag_ratio=0.15))
        self.wait()

