class GenScene(Scene):
    def construct(self):
        title = Text('A* Pathfinding', font_size=36).to_edge(UP)
        size = 0.6
        grid = VGroup()
        for r in range(7):
            for c in range(7):
                sq = Square(side_length=size, color=GRAY, stroke_width=0.5)
                sq.move_to(RIGHT*(c*size - 1.8) + UP*(r*size - 1.8))
                grid.add(sq)
        walls = [(2,1),(2,2),(2,3),(2,4),(4,2),(4,3),(4,4),(4,5)]
        for r,c in walls:
            grid[r*7+c].set_fill(DARK_GRAY, opacity=0.8)
        start = grid[0*7+0]
        end = grid[6*7+6]
        start.set_fill(GREEN, opacity=0.8)
        end.set_fill(RED, opacity=0.8)
        path = [(0,0),(1,0),(1,1),(1,2),(1,3),(1,4),(1,5),(2,5),(3,5),(3,6),(4,6),(5,6),(6,6)]
        self.play(Write(title), Create(grid))
        for r,c in path[1:-1]:
            self.play(grid[r*7+c].animate.set_fill(BLUE, opacity=0.5), run_time=0.2)
        self.wait()

