class GenScene(Scene):
    def construct(self):
        title = Text('Neural Network Forward Pass', font_size=32).to_edge(UP)
        layers = [3, 4, 4, 2]
        neurons = []
        for l, count in enumerate(layers):
            layer = []
            for n in range(count):
                y = (n - (count-1)/2) * 1
                neuron = Circle(radius=0.2, color=BLUE, fill_opacity=0.2).move_to([l*2.5 - 3.75, y, 0])
                layer.append(neuron)
            neurons.append(layer)
        all_neurons = VGroup(*[n for layer in neurons for n in layer])
        edges = VGroup()
        for l in range(len(layers)-1):
            for n1 in neurons[l]:
                for n2 in neurons[l+1]:
                    edges.add(Line(n1.get_center(), n2.get_center(), color=GRAY, stroke_width=0.5))
        self.play(Write(title), Create(edges), Create(all_neurons))
        for l in range(len(layers)):
            highlights = [neurons[l][n].animate.set_fill(YELLOW, opacity=0.8) for n in range(layers[l])]
            self.play(*highlights, run_time=0.5)
            unhighlights = [neurons[l][n].animate.set_fill(BLUE, opacity=0.2) for n in range(layers[l])]
            self.play(*unhighlights, run_time=0.3)
        self.wait()

