class GenScene(Scene):
    def construct(self):
        dots = VGroup()
        for _ in range(500):
            x, y = 0, 0
            for _ in range(20):
                r = np.random.random()
                if r < 0.01:
                    x, y = 0, 0.16*y
                elif r < 0.86:
                    x, y = 0.85*x+0.04*y, -0.04*x+0.85*y+1.6
                elif r < 0.93:
                    x, y = 0.2*x-0.26*y, 0.23*x+0.22*y+1.6
                else:
                    x, y = -0.15*x+0.28*y, 0.26*x+0.24*y+0.44
            dot = Dot([x*0.8, y*0.4 - 2, 0], radius=0.02, color=GREEN)
            dots.add(dot)
        self.play(FadeIn(dots), run_time=2)
        self.play(dots.animate.scale(2).shift(DOWN), run_time=2)
        self.wait()

